import React, { useState } from "react";
import { Spinner } from "reactstrap";

const Loading = props => {
	return (
		<div id="loading-screen">
			<h1>English Premier League Teams</h1>
			<div id="spinners-container">
				<Spinner
					style={{
						width: "3rem",
						height: "3rem"
					}}
					type="grow"
					color="primary"
					className="spinner"
				/>
				<Spinner
					style={{
						width: "3rem",
						height: "3rem"
					}}
					type="grow"
					color="primary"
					className="spinner"
				/>
				<Spinner
					style={{
						width: "3rem",
						height: "3rem"
					}}
					type="grow"
					color="primary"
					className="spinner"
				/>
			</div>
		</div>
	);
};

export default Loading;
