//importing dependencies
import React, { Component } from "react";
import { graphql } from "react-apollo";
import { getPlayersQuery } from "../../Queries/queries.js";

class PlayerTable extends Component {
	displayPlayerDetails() {
		const { teamId } = this.props;
		const { players } = this.props.data;

		// if/else statement used to render data if team is selected, else ... return 'no team selected'
		if (teamId) {
			return <h1>{players.player_name}</h1>;
		} else {
			return <div>No Team Selected...</div>;
		}
	}

	render() {
		console.log(this.props);

		return <div>{this.displayPlayerDetails()}</div>;
	}
}

// here we pass the 'book's ID' from the props as an argument into the options
// parameter of the getBookQuery in order to isolate the single book that was selected
export default graphql(getPlayersQuery, {
	options: props => {
		return {
			variables: {
				id: props.team_id
			}
		};
	}
})(PlayerTable);
